package xyz.viseator.v2ex.activities;import android.os.Bundle;import android.support.annotation.Nullable;import android.support.v7.widget.LinearLayoutManager;import android.support.v7.widget.RecyclerView;import android.support.v7.widget.Toolbar;import android.util.Log;import android.view.View;import android.widget.ImageView;import android.widget.TextView;import cn.bingoogolapple.refreshlayout.BGANormalRefreshViewHolder;import cn.bingoogolapple.refreshlayout.BGARefreshLayout;import cn.bingoogolapple.refreshlayout.BGARefreshViewHolder;import xyz.viseator.v2ex.BaseActivity;import xyz.viseator.v2ex.R;import xyz.viseator.v2ex.http.GetAvatarTask;import xyz.viseator.v2ex.http.GetHtmlDetailTask;/** * Created by viseator on 2016/11/19. */public class DetailActivity extends BaseActivity implements BGARefreshLayout.BGARefreshLayoutDelegate {    private BGARefreshLayout bgaRefreshLayout;    private RecyclerView recyclerView;    private ImageView avatarImageView;    private TextView usernameTextView;    private TextView titleTextView;    private Toolbar toolbar;    private static int page = 1;    private int maxPage;    private static final String TAG = "wudi DetailActivity";    @Override    protected void onCreate(@Nullable Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.detail_activity_layout);        maxPage = (getIntent().getIntExtra("replyNum", -1)) / 101 + 1;        initView();    }    private void initView() {        avatarImageView = (ImageView) findViewById(R.id.detail_avatar);        usernameTextView = (TextView) findViewById(R.id.detail_creator_name);        titleTextView = (TextView) findViewById(R.id.detail_title_textview);        bgaRefreshLayout = (BGARefreshLayout) findViewById(R.id.detail_refreshLayout);        initFreshLayout();        toolbar = (Toolbar) findViewById(R.id.detail_toolbar);        toolbar.setNavigationIcon(getResources().getDrawable(R.drawable.ic_close));        toolbar.setNavigationOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                finish();            }        });        new GetAvatarTask(avatarImageView).execute(getIntent().getStringExtra("avatarURL"));        usernameTextView.setText(getIntent().getStringExtra("name"));        titleTextView.setText(getIntent().getStringExtra("title"));        recyclerView = (RecyclerView) findViewById(R.id.detail_recyclerview);        recyclerView.setLayoutManager(new LinearLayoutManager(this));        Log.d(TAG, getIntent().getStringExtra("URL"));        new GetHtmlDetailTask(recyclerView, this, bgaRefreshLayout, page).execute(getIntent().getStringExtra("URL"));    }    private void initFreshLayout() {        bgaRefreshLayout.setDelegate(this);        BGARefreshViewHolder bgaRefreshViewHolder = new BGANormalRefreshViewHolder(                this, true);        bgaRefreshLayout.setRefreshViewHolder(bgaRefreshViewHolder);        bgaRefreshLayout.setCustomHeaderView(null, true);    }    @Override    public void onBGARefreshLayoutBeginRefreshing(BGARefreshLayout refreshLayout) {        new GetHtmlDetailTask(recyclerView, this, bgaRefreshLayout, page).execute(getIntent().getStringExtra("URL"));    }    @Override    public boolean onBGARefreshLayoutBeginLoadingMore(BGARefreshLayout refreshLayout) {        page++;        if (page <= maxPage) {            new GetHtmlDetailTask(recyclerView, this, bgaRefreshLayout, page).execute(getIntent().getStringExtra("URL"));            return true;        } else {            return false;        }    }}