package xyz.viseator.v2ex.activities;import android.icu.text.LocaleDisplayNames;import android.os.Bundle;import android.support.annotation.Nullable;import android.support.v7.widget.LinearLayoutManager;import android.support.v7.widget.RecyclerView;import android.util.Log;import android.widget.TextView;import java.net.MalformedURLException;import java.net.URL;import xyz.viseator.v2ex.BaseActivity;import xyz.viseator.v2ex.R;import xyz.viseator.v2ex.http.GetContentTask;import xyz.viseator.v2ex.ui.RecyclerViewAdapter;/** * Created by viseator on 2016/11/17. */public class MainActivity extends BaseActivity {    private RecyclerView recyclerView;    private RecyclerViewAdapter recyclerViewAdapter;    private static String TAG = "wudi MainActivity";    private  URL url;    private GetContentTask getContentTask;    @Override    protected void onCreate(@Nullable Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.main_activity_layout);        try {            url = new URL("https://www.v2ex.com/api/topics/hot.json");        } catch (MalformedURLException e) {            Log.d(TAG, "URL error");            e.printStackTrace();        }        initView();        getContentTask = new GetContentTask(this,recyclerView);        getContentTask.execute(url);    }    private void initView() {        recyclerView = (RecyclerView) findViewById(R.id.main_recyclerview);        recyclerView.setLayoutManager(new LinearLayoutManager(this));    }}