package xyz.viseator.v2ex.activities;import android.os.Bundle;import android.support.annotation.Nullable;import android.support.design.widget.TabLayout;import android.support.v4.view.ViewPager;import xyz.viseator.v2ex.BaseActivity;import xyz.viseator.v2ex.R;import xyz.viseator.v2ex.ui.MyPagerAdapter;/** * Created by viseator on 2016/11/17. */public class MainActivity extends BaseActivity {    private final static String TAG = "wudi MainActivity";    private ViewPager viewPager;    private TabLayout tabLayout;    @Override    protected void onCreate(@Nullable Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.main_activity_layout);        initView();    }    private void initView() {        viewPager = (ViewPager) findViewById(R.id.main_viewPager);        viewPager.setAdapter(new MyPagerAdapter(getSupportFragmentManager(), this));        tabLayout = (TabLayout) findViewById(R.id.main_tablayout);        tabLayout.setupWithViewPager(viewPager);        tabLayout.setTabTextColors(getResources().getColor(R.color.LightGrey), getResources().getColor(R.color.White));    }}