package xyz.viseator.v2ex.http;import android.content.Context;import android.os.AsyncTask;import android.support.v7.widget.RecyclerView;import org.jsoup.Jsoup;import org.jsoup.helper.DescendableLinkedList;import org.jsoup.nodes.Document;import org.jsoup.select.Elements;import java.io.IOException;import java.util.List;import cn.bingoogolapple.refreshlayout.BGARefreshLayout;import xyz.viseator.v2ex.data.MainContent;import xyz.viseator.v2ex.ui.MainRecyclerViewAdapter;/** * Created by viseator on 2016/11/24. */public class GetHtmlListTask extends AsyncTask<String, Void, Document> {    private List<MainContent> mainContents;    private RecyclerView recyclerView;    private Context context;    private BGARefreshLayout bgaRefreshLayout;    private static final String TAG = "wudi GetHTMLList";    private static final String URL_PRE = "https://www.v2ex.com";    public GetHtmlListTask(Context context, RecyclerView recyclerView, BGARefreshLayout bgaRefreshLayout) {        this.bgaRefreshLayout = bgaRefreshLayout;        this.recyclerView = recyclerView;        this.context = context;    }    @Override    protected Document doInBackground(String... urls) {        Document doc;        try {            doc = Jsoup.connect(urls[0]).get();            return doc;        } catch (IOException e) {            e.printStackTrace();        }        return null;    }    @Override    protected void onPostExecute(Document document) {        mainContents = new DescendableLinkedList<>();        Elements titles = document.select("span.item_title a");        Elements avatars = document.select("img.avatar");        Elements usernames = document.select("span.small.fade strong");        for (int i = 0; i < titles.size(); i++) {            MainContent mainContent = new MainContent();            mainContent.setNodeAvatar_large(avatars.get(i).attr("src"));            mainContent.setNodeTitle(usernames.get(i).text());            mainContent.setTitle(titles.get(i).text());            mainContent.setMemberAvatar_large(avatars.get(i).attr("src"));            mainContent.setMemberUsername(usernames.get(i).text());            mainContent.setUrl(URL_PRE + titles.get(i).attr("href"));            Elements reply = document.select("a.count_livid[href=" + titles.get(i).attr("href") + "]");            if (!reply.isEmpty()) {                mainContent.setReplies(Integer.parseInt(reply.text()));            } else {                mainContent.setReplies(0);            }            mainContents.add(mainContent);        }        recyclerView.setAdapter(new MainRecyclerViewAdapter(context, mainContents));        bgaRefreshLayout.endRefreshing();    }}