package xyz.viseator.v2ex.ui;import android.content.Context;import android.support.design.widget.CoordinatorLayout;import android.support.v7.widget.CardView;import android.util.AttributeSet;import android.view.View;/** * Created by viseator on 2016/11/20. */public class CardViewHideBehavior extends CoordinatorLayout.Behavior<CardView> {    public CardViewHideBehavior(Context context, AttributeSet attrs) {        super();    }    @Override    public boolean onStartNestedScroll(CoordinatorLayout coordinatorLayout, CardView child, View directTargetChild, View target, int nestedScrollAxes) {        return true;    }    @Override    public void onNestedScroll(CoordinatorLayout coordinatorLayout, CardView child, View target, int dxConsumed, int dyConsumed, int dxUnconsumed, int dyUnconsumed) {        super.onNestedScroll(coordinatorLayout, child, target, dxConsumed, dyConsumed, dxUnconsumed, dyUnconsumed);        if (dyConsumed > 0 ) {            child.setVisibility(View.INVISIBLE);        } else if (dyConsumed < 0 ) {            child.setVisibility(View.VISIBLE);        }    }}