package xyz.viseator.v2ex.ui;import android.content.Context;import android.support.v4.app.Fragment;import android.support.v4.app.FragmentManager;import android.support.v4.app.FragmentStatePagerAdapter;import android.view.View;import android.view.ViewGroup;import java.util.List;import xyz.viseator.v2ex.R;/** * Created by viseator on 2016/11/24. */public class MyPagerAdapter extends FragmentStatePagerAdapter {    private Context context;    public MyPagerAdapter(FragmentManager fm, Context context) {        super(fm);        this.context = context;    }    @Override    public Object instantiateItem(ViewGroup container, int position) {        return super.instantiateItem(container, position);    }    @Override    public void destroyItem(ViewGroup container, int position, Object object) {        super.destroyItem(container, position, object);    }    @Override    public boolean isViewFromObject(View view, Object object) {        return super.isViewFromObject(view, object);    }    @Override    public Fragment getItem(int position) {        MainRecyclerViewFragment mainRecyclerViewFragment = new MainRecyclerViewFragment();        mainRecyclerViewFragment.setPosition(position);        return mainRecyclerViewFragment;    }    @Override    public CharSequence getPageTitle(int position) {        switch (position) {            case 0:                return context.getString(R.string.latest);            case 1:                return context.getString(R.string.hotest);        }        return null;    }    @Override    public int getCount() {        return 2;    }}