package xyz.viseator.v2ex.ui;import android.content.Context;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.TextView;import xyz.viseator.v2ex.R;/** * Created by viseator on 2016/11/17. */public class RecyclerViewAdapter extends RecyclerView.Adapter<RecyclerViewAdapter.ViewHolder> {    private Context context;    public static class ViewHolder extends RecyclerView.ViewHolder {        ImageView imageView;        TextView textViewContentSource;        TextView textViewTitle;        TextView textViewContent;        TextView textViewReply;        public ViewHolder(View itemView) {            super(itemView);            imageView = (ImageView) itemView.findViewById(R.id.content_logo);            textViewContentSource = (TextView) itemView.findViewById(R.id.content_source);            textViewTitle = (TextView) itemView.findViewById(R.id.content_title);            textViewContent = (TextView) itemView.findViewById(R.id.content);            textViewReply = (TextView) itemView.findViewById(R.id.content_reply);            itemView.setOnClickListener(new View.OnClickListener() {                @Override                public void onClick(View view) {                }            });        }    }    public RecyclerViewAdapter.ViewHolder onCreateViewHolder(ViewGroup parent, int viewType) {        View v = LayoutInflater.from(context).inflate(R.layout.recyclerview_content_layout, parent, false);        RecyclerViewAdapter.ViewHolder vh = new RecyclerViewAdapter.ViewHolder(v);        return vh;    }    public RecyclerViewAdapter(Context context) {        this.context = context;    }    @Override    public void onBindViewHolder(RecyclerViewAdapter.ViewHolder holder, int position) {    }    @Override    public int getItemCount() {        return 0;    }}